import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Slider from "react-slick";
import styled from 'styled-components';
import { mobile } from '../Responsive';
import { Link } from 'react-router-dom';
import { FavoriteBorderOutlined, SearchOutlined,  } from '@material-ui/icons';


const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
const Container = styled.div`

margin: 5px;
  min-width: 100px;
  height: 350px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  transition: all 0.5s ease;
  &:hover {
    opacity: 1;
    transform: scale(1.2);
  }
 
`;
const Image = styled.img`
    width: 300px;
  height: 300px;
  object-fit: cover;
  ${mobile({height:"100px",width:"100px"})}
`;
const Info = styled.div`
   opacity: 1;
  width: 80%;
  height: 80%;
  position:a;
  top: 0;
  left: 0;
  z-index: 3;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: all 0.5s ease;
  ${mobile({width:"50%",height:"50%",})}
`;
const Icon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  margin: 10px;
  transition: all 0.5s ease;
  &:hover{
      border-color: #e9f5f5;
      transform: scale(1.1);
  }

 ${mobile({height:"20px",width:"20px",margin:"1px"})}

`;



function SliderImages() {
  const [products,setProducts] = useState([]);
  useEffect(()=>{
    const getProducts = async ()=>{
     try{
       const res = await axios.get("http://localhost:5000/api/products");
       setProducts(res.data);
       
     }catch(err){

     }
    }
    getProducts();
 },[]);
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div className="slider-container">
      <Slider {...settings}>
        {products.map((product, index) => (
          <Wrapper>
          <Container key={index}  >
           
            {product.title}
            <Image src={product.img} alt={`Slide ${index}`} />
            <Info>
     
      <Icon>
        <Link to={`/product/${product._id}`}>
        <SearchOutlined />
        </Link>
        
      </Icon>
      <Icon>
         <FavoriteBorderOutlined/>
      </Icon>
     </Info>
          </Container>
          </Wrapper>
        ))}
      </Slider>
    </div>
  );
};


export default SliderImages
