import React from 'react'
import { useSelector } from 'react-redux'
import styled from 'styled-components'
import ProductItem from './ProductItem';
import { DeleteOutlined } from '@material-ui/icons';


const Container = styled.div`
  padding: 10px;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  background-color: #f5fefd;
`
function Wishes() {
  const handleDeleteProduct = (e)=>{
    e.preventDefault();
    const productId = e.target.id
    const product = cart.products.find(product=> product._id === productId);
   try{

    dispatch(removeProductSuccess({...product,totalPrice}));
   
    
   }catch(err){}
   
  }
  const products = useSelector(state=>state.customer.currentCustomer.wish);
  return (
    <Container>
      {products.map(item=>(<>
      <ProductItem item={item} key={item.id}/>
        <DeleteOutlined id={item.id} onClick={handleDeleteProduct} style={{marginTop:'10px',cursor:'pointer',color:'red'}}/></>))
      }
    </Container>
  )
}

export default Wishes
