import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { userRequest } from '../requestMethod';
import { resetCustomerCart } from '../redux/apiCalls';
function PayCash() {
  
  const cart = useSelector((state)=>state.cart);
  const [orderId, setOrderId] = useState(null);
  const currentCustomer = useSelector((state) => state.customer.currentCustomer);
  const dispatch = useDispatch();
  
  useEffect(() => {
    const createOrder = async () => {
      try {
        const res = await userRequest.post("/orders", {
          userId: currentCustomer._id,
          products: cart.products.map((item) => ({
            productId: item._id,
            title: item.title,
            img: item.img,
            size: item.size,
            color: item.color,
            quantity: item._quantity,
          })),
          amount: cart.total,
          address: 'none',
          payment: 'online',
          paymentStatus: 'done'
        });
      
        setOrderId(res.data._id);
        resetCustomerCart(dispatch);
      } catch {}
    };
  !orderId && createOrder();
  }, []);
  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {orderId
        ? `Order has been created successfully. Your order number is ${orderId}`
        : `Successfull. Your order is being prepared...`}
      <button onClick={()=>{window.location.href = '/'}} style={{ padding: 10, marginTop: 20 }}>Continue Shopping</button>
    </div>
  )
}

export default PayCash
