import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import Announcement from '../components/Announcement';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { Add, DeleteOutlined, Remove } from '@material-ui/icons';
import { mobile } from '../Responsive';
import { useDispatch, useSelector } from 'react-redux';
import StripeCheckout from 'react-stripe-checkout';
import { userRequest } from '../requestMethod';
import { useNavigate } from 'react-router-dom';
import { removeProductSuccess } from '../redux/cartRedux';





const KEY = "pk_test_51Oo2gmCF9ap7lAyw3JKsmRoKT0njH1v22ggEeBfkgqv8mo6zXNMWVUDPl9qE5CjdaXhbVCbeGgUenYkK5huXeeR200bHOlVIU9";
const Container = styled.div`
  
`;
const Wrapper = styled.div`
  padding: 20px;
  ${mobile({padding:"10px"})}
`;
const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`;
const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;
const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  cursor: pointer;
  border: ${props=>props.type==="filled" && "none"};
  background-color: ${props=>props.type==="filled" ? "black" : "transparent"};
  color: ${props=>props.type==="filled" && "white"};
`;
const TopTexts = styled.div`
  ${mobile({display:"none"})}
`;
const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  margin: 0px 10px;
`;
const Bottom = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  ${mobile({flexDirection:"column"})}
`;
const Info = styled.div`
  flex: 3;
`;
const Product = styled.div`
  display: flex;
  justify-content: space-between;
  ${mobile({flexDirection:"column"})}
`;
const ProductDetail = styled.div`
  flex: 2;
  display: flex;
`;
const Image = styled.img`
  width: 200px;
  margin-top: 5px;
`;
const Details = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`;
const ProductName = styled.span`
  
`;
const ProductId = styled.span`
  
`;
const ProductColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${props=>props.color};
`;
const ProductSize = styled.span`
  
`;
const PriceDetail = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`

const ProductAmountContainer = styled.div`
display: flex;
align-items: center;
margin-bottom: 20px;
`;
const ProductAmount = styled.div`
  font-size: 24px;
  margin: 5px;
  ${mobile({margin:"5px 15px"})}
`;
const ProductPrice = styled.div`
  font-size: 30px;
  font-weight: 200;
  ${mobile({marginBottom:"20px"})}
`;
const Hr = styled.hr`
  background-color: #eee;
  border: none;
  height: 1px;
`
const Summary = styled.div`
  flex: 1;
  border: solid 0.5px lightgray;
  border-radius: 10px;
  padding: 20px;
  height: 60vh;

`;
const SummaryTitle = styled.h1`
font-weight: 200;
`;
const SummaryItem = styled.div`
  margin: 30px 0;
  display: flex;
  justify-content: space-between;
  font-weight: ${props=>props.type==="total" && "500"};
  font-size: ${props=>props.type==="total" && "24px"};
`;
const SummaryItemText = styled.div`
  
`;
const SummaryItemPrice = styled.div`

`;
const Button = styled.button`
width: 100%;
padding: 10px;
background-color: black;
color: white;
`;


const Cart = () => {

  const cart = useSelector(state=>state.cart);
  const [totalPrice, setTotalPrice] = useState(0);
  const [stripeToken,setStripeToken] = useState(null);
  const dispatch = useDispatch();
  
  const onToken = (token)=>{
    setStripeToken(token);
  };
  let  navigate = useNavigate();

  useEffect(() => {
    const makeRequest = async () => {
      try {
        const res = await userRequest.post("/checkout/payment", {
          tokenId: stripeToken.id,
          amount: ((cart.total * 100).toFixed()),
          
        });
        
        navigate("/success",{replace:true,state:{cart:cart,stripeData:res.data}
        });
          
         
      } catch {}
    };
    stripeToken && makeRequest();
  }, [stripeToken, cart,navigate]);

  

  const handleDeleteProduct = (e)=>{
    e.preventDefault();
    const productId = e.target.id
    const product = cart.products.find(product=> product._id === productId);
   try{

    dispatch(removeProductSuccess({...product,totalPrice}));
   
    
   }catch(err){}
   
  }

  useEffect(()=>{
    if (cart && cart.products) {
      const sum = cart.products.reduce((acc, product) => acc + product.price * product.quantity, 0);
      setTotalPrice(sum);
      
    }
  },[cart])
  return (
   <Container>
    <Announcement/>
    <Navbar/>
    <Wrapper>
      <Title>YOUR BAG</Title>
      <Top>
        <TopButton >CONTINUE SHOPING</TopButton>
        <TopTexts>
          <TopText>SHOPING BAG(2)</TopText>
          <TopText>YOUR WISHLIST(0)</TopText>
        </TopTexts>
        <TopButton type="filled">CHECK OUT NOW</TopButton>
      </Top>
      <Bottom>
        <Info>
          {cart.products.map((product)=>(
            <Product>
            <ProductDetail>
              <Image src={product.img}/>
              <Details>
                <ProductName><b>Product:</b> {product.title}</ProductName>
                <ProductId><b>ID:</b>{product._id}</ProductId>
                <ProductColor color={product.color}/>
                <ProductSize><b>Size:</b> {product.size}</ProductSize>
                <DeleteOutlined id={product._id} onClick={handleDeleteProduct} style={{marginTop:'10px',cursor:'pointer',color:'red'}}/>
              </Details>
            </ProductDetail>
            <PriceDetail>
              <ProductAmountContainer>
                <Add/>
                <ProductAmount>{product.quantity}</ProductAmount>
                <Remove/>
              </ProductAmountContainer>
              <ProductPrice>$ {product.price*product.quantity}</ProductPrice>
            </PriceDetail>
          </Product>
          

          ))}
          <Hr/>
          </Info>
        <Summary>
          <SummaryTitle>ORDER SUMMARY</SummaryTitle>
          <SummaryItem>
            <SummaryItemText>Subtotal</SummaryItemText>
            <SummaryItemPrice>$ {totalPrice}</SummaryItemPrice>
          </SummaryItem>
          <SummaryItem>
            <SummaryItemText>Estimated Shipping</SummaryItemText>
            <SummaryItemPrice>$ 5.90</SummaryItemPrice>
          </SummaryItem>
          <SummaryItem>
            <SummaryItemText>Estimated Discount</SummaryItemText>
            <SummaryItemPrice>$ 5.9</SummaryItemPrice>
          </SummaryItem>
          <SummaryItem  type="total">
            <SummaryItemText>Total</SummaryItemText>
            <SummaryItemPrice>$ {totalPrice}</SummaryItemPrice>
          </SummaryItem>
          <StripeCheckout
              name="Lama Shop"
              image="https://avatars.githubusercontent.com/u/1486366?v=4"
              billingAddress
              shippingAddress
              description={`Your total is $${cart.total}`}
              amount={cart.total * 100}
              token={onToken}
              stripeKey={KEY}
            >
              <Button>CHECKOUT NOW</Button>
            </StripeCheckout>
        </Summary>
      </Bottom>
    </Wrapper>
    <Footer/>

   </Container>

  )
}

export default Cart
