import { FavoriteBorderOutlined, SearchOutlined, ShoppingCartOutlined } from '@material-ui/icons'
import { Link } from 'react-router-dom';
import styled from 'styled-components'
import { addProduct } from '../redux/cartRedux';
import { useDispatch } from 'react-redux';
import { mobile } from '../Responsive';
import { addWish } from '../redux/userRedux';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
const Info = styled.div`
  opacity: 1.5;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  background-color: transparent;
  z-index: 3;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: all 0.5s ease;
  &:hover{
    
  }
`;
const Container = styled.div`
  
  margin: 5px;
  min-width: 120px;
  height: 170px;
  padding: 3px;
  margin: 20px;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  display: inline-block;
  transition: all 0.5s ease;
  &:hover {
    opacity: 1;
    transform: scale(1.1);
    background-color: rgb(0,0,0,0.2);
  }
`

const Image = styled.img`
  height: 70%;
  border-radius: 5px;
  z-index: 2;
  ${mobile({height:'50%'})}
`;

const Icon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: transparent;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  margin: 10px;
  transition: all 0.5s ease;
  
  &:hover{
      background-color: wheat;
      transform: scale(1.1);
      display: inline-block;
  }

`;
const Product = styled.div`
 width: 100px;
 height: 100px;
 display : flex;
 flex-direction: column;
`;
const Title = styled.h1`
  font-weight: 200;
  font-size: 10px;
`;

const Price = styled.span`
  font-weight: 300;
  font-size: 20px;
`;
const FilterContainer = styled.div`
  margin: 5px 0px;
  width: 100%;
  height: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  ${mobile({width:"100%"})}
`;
const Filter = styled.div`
   display: flex;
   align-items: center;
   margin-right:10px;
   margin-bottom:10px;
`;

const FilterColor = styled.div`
 width: 15px;
 height: 15px;
 border: 1px solid black;
 border-radius: 50%;
 background-color: ${props=>props.color};
 margin: 0px 5px;
 cursor: pointer;
 transition: all 0.5s ease;
  &:hover{
      
      transform: scale(1.2);
  }
`;

const FilterSize = styled.span`
  font-size: 12px;
  font-weight: 600;
  margin: 0 10px ;
  cursor: pointer;
  transition: all 0.5s ease;
  &:hover{
      
      transform: scale(1.5);
  }
`;


const ProductItem = ({item}) => {
 const dispatch = useDispatch();
 const quantity = 1;
  const addToCart = ()=>{
      if(item.selectedColor === 'none'){
        toast.info('Please Select Size and Color!', {
          position: "top-center",
          autoClose: 3000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "light",
          
          });
      }else{
        
        dispatch(addProduct({...item,quantity}));  
        item.selectedColor = 'none';
        toast.success('Added !', {
          position: "top-center",
          autoClose: 3000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "light",
          
          });
      }
      
  }
  
  const addToWish = ()=>{
    dispatch(addWish(item));
  }
  
  return (
    <Wrapper>
   <Container>
     <Image src={item?.img}/>
     <Info>
      <Icon>
         <ShoppingCartOutlined style={{color:'green'}} onClick={addToCart}/>
      </Icon>
      <Icon>
        <Link to={`/product/${item?._id}`}>
        <SearchOutlined />
        </Link>
        
      </Icon>
      <Icon>
         <FavoriteBorderOutlined style={{color:'red'} } onClick={addToWish}/>
      </Icon>
     </Info>
     <Title>{item?.title}</Title>
   <Price>{item?.price} SAR</Price>
   
   <Product>
   
   <FilterContainer>
    <Filter>
      
      {
        item.color.map((c)=>
      <FilterColor onClick={()=>{item.selectedColor = c}} color={c} key={c} />)
      
      }
      
    </Filter>
   </FilterContainer>
   <FilterContainer>
    <Filter>
      
      {
        item.size?.map((c)=>
      <FilterSize onClick={()=>{item.size = c;}
      
      }  key={c}>
        {c}
        </FilterSize>
        )
      }
    </Filter>
   </FilterContainer>
  </Product>
  </Container>
  <ToastContainer/>
  </Wrapper>
 
  )
}

export default ProductItem
