import { FavoriteBorderOutlined, SearchOutlined, ShoppingCartOutlined } from '@material-ui/icons'
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components'
import { addProduct } from '../redux/cartRedux';
import { useDispatch } from 'react-redux';
import { mobile } from '../Responsive';

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
`;
const Info = styled.div`
  opacity: 0;
  width: 80%;
  height: 80%;
  position: absolute;
  top: 0;
  left: 0;
  background-color: rgb(0,0,0,0.2);
  z-index: 3;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: all 0.5s ease;
`;
const Container = styled.div`
  
  margin: 5px;
  min-width: 100px;
  height: 200px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  &:hover ${Info}{
    opacity: 1;
  }
`
const Circle = styled.div`
width: 120px;
height: 120px;
border-radius:50%;
background-color: white;
position: absolute;
`;
const Image = styled.img`
  height: 50%;
  border-radius: 5px;
  z-index: 2;
`;

const Icon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  margin: 10px;
  transition: all 0.5s ease;
  &:hover{
      border-color: #e9f5f5;
      transform: scale(1.1);
  }

`;
const Product = styled.div`
 width: 200px;
 height: 100px;
 display : flex;
 flex-direction: column;
`;
const Title = styled.h1`
  font-weight: 200;
  font-size: 10px;
`;

const Price = styled.span`
  font-weight: 300;
  font-size: 20px;
`;
const FilterContainer = styled.div`
  margin: 5px 0px;
  width: 100%;
  height: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  ${mobile({width:"100%"})}
`;
const Filter = styled.div`
   display: flex;
   align-items: center;
   margin-right:10px;
   margin-bottom:10px;
`;

const FilterColor = styled.div`
 width: 15px;
 height: 15px;
 border: 1px solid black;
 border-radius: 50%;
 background-color: ${props=>props.color};
 margin: 0px 5px;
 cursor: pointer;
`;
const ProductColor = styled.div`
  width: 20px;
  height: 20px;
  margin-right: 10px;
  border-radius: 3px;
  border: 1px solid gray;
  background-color: ${props=>props.color};
`;
const FilterSize = styled.span`
  font-size: 12px;
  font-weight: 600;
  margin: 0 10px ;
  cursor: pointer;
`;
const ItemDetails = styled.div`
height: 15px;
width: 150px;
display: flex; 
align-items: center;
`;
const ItemDetailsText = styled.span`
  font-size: 10px;
  font-weight: 600;
  margin-right: 3px;
`;
const ItemDetailsColor = styled.div`
  height: 10px;
  width: 10px;
  border-radius: 50%;
  border: 1px solid black;
  background-color: red;
`

const ProductItem = ({item}) => {
  const [color,setColor] = useState([]);
  const [itemSize,setItemSize] = useState()
 const dispatch = useDispatch();
 const quantity = 1;
  const addToCart = ()=>{
    dispatch(addProduct({...item,quantity}));
  }

  useEffect(()=>{
    const setItemSize = (c)=>{
        itemSize = c;
        console.log(itemSize);
    }
  },[itemSize]);

  return (
    <Wrapper>
      <ItemDetails>
      <ItemDetailsText>Color:</ItemDetailsText>
      <ItemDetailsColor></ItemDetailsColor>
      </ItemDetails>
      <ItemDetails>
      <ItemDetailsText>Size:{itemSize}</ItemDetailsText>
      <ItemDetailsText></ItemDetailsText>
      </ItemDetails>
   <Container>
     <Circle/>
     <Image src={item.img}/>
     <Info>
      <Icon>
         <ShoppingCartOutlined onClick={addToCart}/>
      </Icon>
      <Icon>
        <Link to={`/product/${item._id}`}>
        <SearchOutlined />
        </Link>
        
      </Icon>
      <Icon>
         <FavoriteBorderOutlined/>
      </Icon>
     </Info>
   </Container>
   <Product>
   <Title>{item.title}</Title>
   <Price>{item.price} SAR</Price>
   <FilterContainer>
    <Filter>
      
      {
        item.color?.map((c)=>
      <FilterColor onClick={()=>{item.color = c;}} color={c} key={c} />)
      }
    </Filter>
   </FilterContainer>
   <FilterContainer>
    <Filter>
      
      {
        item.size?.map((c)=>
      <FilterSize onClick={()=>{item.size = c; }}  key={c}>
        {c}
        </FilterSize>
        )
      }
    </Filter>
   </FilterContainer>
  </Product>
  </Wrapper>
  )
}

export default ProductItem
