import { ArrowLeftOutlined, ArrowRightOutlined } from '@material-ui/icons'
import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  
  margin-top: 10px;
  position: relative;
`
const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: #ebe1e1;
  border-radius: 50%;
  display: flex;
  align-content: center;
  justify-content: center;
  align-items: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${props=> props.direction === "left" && "10px"};
  right: ${props=> props.direction === "right" && "10px"};
  margin: auto;
  cursor: pointer;
  opacity: 0.5;

`;

const Wrapper = styled.div`
  height: 100%;
`;
const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
`;
const Image = styled.ima`
height: 80%;
`
const ImgContainer = styled.div`
height: 100%;
flex: 1;
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 50px;
`;

const Slider = () => {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowLeftOutlined/>
      </Arrow>
       <Wrapper>
       
        <ImgContainer>
         <Image src="C:\Users\Abdu\OneDrive\Desktop\React\hamburger-with-cheese-lettuce-tomatoes.jpg"/>
        </ImgContainer>

        <InfoContainer>

        </InfoContainer>
       
       </Wrapper>
      <Arrow direction="right">
        <ArrowRightOutlined/>
      </Arrow>

    </Container>
  )
}

export default Slider
